			var inputValue = event.charCode || event.keyCode;
			if(!(inputValue >= 65 && inputValue <= 122) &&(inputValue != 32 && inputValue != 0 && inputValue != 8)){

				event.preventDefault();		
			}
			else if((inputValue == 91 || inputValue == 92 || inputValue == 93 || inputValue == 94 || inputValue == 95 || inputValue == 96)){
				event.preventDefault();
			}
		});



	$(document).on('keypress keydown keyup','.alphanumericOnly',function (e) {
			var regex = new RegExp("^[a-zA-Z0-9\b]+$");
			var str = String.fromCharCode(!e.charCode ? e.which : e.charCode);
			if (regex.test(str)) {
				return true;
			}

			e.preventDefault();
			return false;
		});


	});

