$(document).ready(function() {  
	if('0' == 1) {
		$('.subProfile').hide();
	}
	$("button.saveCert").button({disabled:true});
	$(document).on("focus", ".certifyDt", function(){ 
		$(this).datepicker({ 
		dateFormat: dateFormat(),
		changeMonth: true,
		maxDate: new Date(),
		changeYear: true
		});
		$.datepicker._clearDate($(this).closest('tr').find('input[class*=expiryDt]'));
	});
	$(document).on("focus", ".expiryDt", function(){ 
		var fromDate = new Date();
		fromDate = $(this).closest('tr').find('input[class*=certifyDt]').datepicker('getDate'); 
		$(this).datepicker("destroy");
		$(this).datepicker({ minDate: fromDate});
		$(this).datepicker({ maxDate: false}); 
    });

	$(document).on('change','#certType',function(){
		if($(this).val()==2){
			$(this).closest('tr').find('.extCert').show();
		}else{
			$(this).closest('tr').find('.extCert').hide();
		}
	});

	$('#addCert').click(function(){
	  $('#resetBtn').removeAttr('disabled');
    });	
});
