	$(document).on('click','.editSkill',function(e){	
		e.preventDefault();
		var exp = $(this).closest('tr').find('.skillExp').prev().html();
		if(exp == 0 || exp == '' || exp == null){
			$(this).closest('tr').find('.skillExp').val('');
			$(this).closest('tr').find('.datepicker').val('');
		}
		$(this).closest('tr').find('input,select').removeClass('displayNone');
		$(this).closest('tr').find('span').addClass('displayNone');
		var id=$(this).closest('tr').find('span#skillgrpdata').html();
		var i =$(this).closest('tr').find('span#skilliddata').html();
		$(this).closest('tr').find('select#skillGroupID').val(id);
		$('#addSkill').next().removeAttr('disabled');
		$("tbody#skillRecords button.delSkill,tbody#skillRecords button.saveSkill, button.editApproveSkill, tbody#skillRecords button.editSkill, button#addSkill").button({disabled:true});	
