			$('button#cancelBtn').button({disabled:false});
			$(this).closest('tr').find('.skillName, .skillExp, .datepicker').removeAttr('disabled');
			$(this).closest('tr').find('#skillGroupID, #skillId, #skillType').removeAttr('disabled');
			$(this).closest('tr').find('select#skillId').next().removeAttr('disabled');
			var stars =$(this).closest('tr').find('span#starData').html();
			//$(this).closest('tr').find('input.editable').removeAttr('readonly');
			$(this).closest('tr').find('div.starContainer').replaceWith('<div class="starEdit"><div class="starrr" id="star__data__"></div><input type="hidden" name="skillRating" id="starr__data__input" value ="' +stars+'"/></div>');
			//$(this).closest('tr').find('input.datepicker').attr('readonly','readonly');
			$('.starrr').starrr({
				max: 5,
				rating: stars,
				change: function(e, value){
				$(this).next('input').val(value);
				}
				});
		$(this).closest('tr').find('#skillId').find('option').remove();
		$.ajax({
					type: "POST",
					url: '/employees/skillGroupSearch' ,
					data: 'groupId = '+ id,
					cache: false,
					dataType: 'json',
					success: function(response) {
						$('select#skillId').find('option').remove().end();

						$.each(response, function(key, value) {              
							$('<option>').val('').text('select');
							$('<option>').val(key).text(value).appendTo($("select#skillId:enabled"));
						});
						$(("select#skillId:enabled")).val(i);
					} 
		});
		return false;
	});

    $(document).on('click','#cancelBtn',function(){	
			$("tbody#skillRecords").closest('form')[0].reset();
			//$("tbody#skillRecords tr:last-child").find('select, input').attr('disabled','disabled');
			var self_rating = $('.starEdit').prev('span#starData').html();


			if(self_rating == undefined){
				$('div.starrr i.fa-star').addClass('fa fa-lg fa-star-o');	
			}else{
			$('div.starEdit').replaceWith('<div class="starEdit"><div class="starrr" id="star__data__"></div><input type="hidden" name="skillRating" id="starr__data__input" value ="' +self_rating+'"/></div>');
			$('.starrr').starrr({
				max: 5,
				rating: self_rating,
				change: function(e, value){
			    $(this).next('input').val(value);
				}
				});
			} 
			var id = $("select#skillGroupID:enabled").prev().prev().html();
			var skillid= $("select#skillId:enabled").prev().prev().html();
			$("select#skillId:enabled").find('option').remove();
			$.ajax({
					type: "POST",
					url: '/employees/skillGroupSearch' ,
					data: 'groupId = '+ id,
					cache: false,
					dataType: 'json',
					success: function(response) {
						$.each(response, function(key, value) {              
							$('<option>').val('').text('select');
							$('<option>').val(key).text(value).appendTo($("select#skillId:enabled"));
						});
						if(skillid=='' || skillid==null){
							$(("select#skillId:enabled")).append("<option >&lt;&lt;  Please Select &gt;&gt;</option>");
						}else{
							$(("select#skillId:enabled")).val(skillid);
						}
					} 
			});
    }); 
	$('button.newSkillDel').click(function(){
