function resetprop(){
	$('#addSkill').removeAttr('disabled');
	$('#addSkill').next().attr('disabled',true);
	$('tr').find('.cancelEdtSkill').trigger('click');
	$('tr').find('.cancelEdtApproveSkill').trigger('click');
	$("tbody#skillRecords button.editSkill, button.editApproveSkill").button({disabled:false});
	$("tbody#skillRecords button.delSkill").button({disabled:false});	
}/**/

