	function withDraw(){
		$('#sbmBtn').attr("disabled",true);
		if($('#withdrawReason').val().replace(/^\s+|\s+$/g, '').length == 0){
			$('#withdrawReason').addClass('highlight_item_error');
			$('div.sbmErr').removeClass('clear');
		}else{
			var notes = $("#withdrawReason").val().trim();
			var exitId = $("#exitId").val();
			var action = $("#withdrawSection").attr('status');
			$.ajax({
				type: "POST",
				dataType: 'html',
				data: 'cancel_notes=' + encodeURIComponent(notes)+'&exitId='+exitId,	
				url: '/employees/profile/employment/resignation?action='+action,	
				async: true,
				success: function(withdrawResponse){
					if(withdrawResponse)	{
						var withdrawObj = JSON.parse(withdrawResponse);
						$("#resignStatus").val(withdrawObj.activeExit.EmpExit.status);
						$("#cancel_note").removeClass('clear');
						$("#withdrawSection").addClass('clear');							
						$("#cancelNote").html(withdrawObj.activeExit.EmpExit.cancel_notes);	
						if(withdrawObj.activeExit.EmpExit.status == 'Withdrawn - Heat Move') {
							$('#resignSuccessMsg').html("Your Resignation Withdraw request is submitted.");
						} else { 
							$('#resignSuccessMsg').html("Your Resignation Withdraw request is submitted and your supervisor, HR are being notified of the same.");
						}
						$('#resignSuccess').removeClass('clear');
						$('#resignSuccess').fadeIn();
						setTimeout(function(){$('#resignSuccess').fadeOut("slow");}, 3000);	
						$("#listOfActions").addClass('clear');
						$("#noActions").removeClass('clear');
					}
					$('div.withDrawDlg').dialog('close');
					$("#sbmBtn").removeAttr("disabled");
					$(".withdrawYesBtn").removeAttr("disabled");
				}
			});
		}
	}/**/

$(document).ready(function(){	

			$('#actBtn').click(function(){

				var isError = false;
				if ($('#nextAction').val() == "") {
                $('#nextAction').addClass('highlight_item_error');
                isError = true;
            } else {
                $('#nextAction').removeClass('highlight_item_error');
            }


            if (isError) {
                $('.resignErrMsg').removeClass('clear');
                return false;
            } else {
				 $('.resignErrMsg').addClass('clear');

			}


			var nextAction = $('#nextAction').val();

			if(nextAction.replace(/^\s+|\s+$/g, '').length > 0){
				if((nextAction == "withdraw") || (nextAction == "withdrawnHeatMove")){
					$("#withdrawSection").removeClass('clear');
					$("#withdrawSection").attr('status',nextAction);
				}
				else if(nextAction == "sbmFeedback"){
					var feedbackUrl = 'http://10.11.12.141:92/employees/profile/employment/resignation?action=' + nextAction;
					window.location.href = feedbackUrl;
				}

			}else{
				return false;
			}
		});



	});

