	function feedRelation(param){
		$relationData = $(param).val();
		$(param).closest('td').find('input#relationValue').val($relationData);
	}

	function validateP(event){

		if(event.which != 8 &&  isNaN(String.fromCharCode(event.which))){
			event.preventDefault(); //stop character from entering input
	}


	}
	function validateC(event){

		if(event.which != 8 && event.which != 43 && event.which != 45 &&  isNaN(String.fromCharCode(event.which))){
			event.preventDefault(); //stop character from entering input
	}


	}
    function clickSave(elem){  	
		var isError = false;
		var validation = false;
		var priority = /^[0-9]+$/;
		var contact = /^[0-9+-\s]+$/;
		var alphabets = /^([a-zA-Z .]{3,50})$/;
		var tmp = {};

		$(elem).closest('tr').find('td:has(input.validate)').each(function() {
				var textBox = $(this).find('input.validate');
				var selectBox = $(elem).closest('tr').find('#emergencyContacts');
				var val = textBox.val().trim();


			if(val.length == 0){
				textBox.addClass('highlight_item_error');
				isError = true;	
			}else{
					textBox.removeClass('highlight_item_error');

			}	
			if(selectBox.val().length == 0){
				selectBox.addClass('highlight_item_error');
				isError = true;	
			}else{
					selectBox.removeClass('highlight_item_error');
			}	
		});

		if(isError) {
			$('div#validationErrMsg').removeClass('clear');
			return false;
		} else {

		$(elem).closest('tr').find('td:has(input)').each(function() {
			   var row = elem;
			   var values = "";
			   tmp[$(this).find('input').attr('name')] = $(this).find('input').val();

		});
		$(elem).closest('tr').find('td:has(select)').each(function() {
			   tmp[$(this).find('select').attr('name')] = $(this).find('select').val();

		});

			   var contactRecords = JSON.stringify(tmp); 

			   $('#econLoadingImg').removeClass('displayNone');
				$('#econLoadingImg').show();
			// AJAX Code To Submit Form.
				$.ajax({
				type: "POST",
				dataType: 'html',
				data: 'contactRecords=' + encodeURIComponent(contactRecords),	
				url: "/employees/profile/personal/general",	
				async:true,
				cache: false,
				success: function(response){ 
					 $('#econLoadingImg').addClass('displayNone');
					   $('#econLoadingImg').hide(); 
					if(response){
						$('#qi').html(response);
						$('input.editable').attr('disabled', 'disabled');
						$('select.editable').attr('disabled', 'disabled');
						$("button.saveBtn, button.cancelEdtEcon").button({disabled:true});
						$("button.edtBtn").button({disabled:false});
						$('#padder').load('/employees/profile/personal/general #qi');
						$('#tableEcontact').dataTable().fnDestroy();
						$('#tableEcontact').dataTable();
						$('div#validationErrMsg').addClass('clear'); 
						$('#successAlert').removeClass('clear');
						$('#successAlertMsg').html("Emergency contact saved successfully.");
						$('#failAlert').addClass('clear');
						$('#successAlert').fadeIn();
						setTimeout(function(){$('#successAlert').fadeOut("slow");}, 2000);

						} 
					} 
				});

		}

	}

	function displayAddress() {

		if(addressDisplay == 0) {
			$.ajax({
				type: "POST",
				url: "/employees/profile/personal/general",		
				dataType: 'html',
				data: {"get":"EmpAddress"},	
				async:true,
				cache: false,
				beforeSend:function() {
					$('#addloadingImg').removeClass('displayNone');
					$('#addloadingImg').show();
				},
				success: function(response){
					$('#addloadingImg').addClass('displayNone');
					$('#addloadingImg').hide();
					if(response){
							addressDisplay = 1;
							$('#addressDiv').html(response); 
					}
					else{ 
						$('#successAlert').addClass('clear');
						$('#failAlert').removeClass('clear');
						$('#failedAlertMsg').html("Error while fetchingdata . Please try after sometime.");
						$('#failAlert').fadeIn();
						setTimeout(function(){$('#failAlert').fadeOut("slow");}, 2000); 
					}
				} 
			});
		} 						
	}


	function empDependents() {
		$('#tblMediClaim').dataTable().fnDestroy();
		 $('#tblMediClaim').DataTable( {
        "columnDefs": [
            {
                "targets": [ 5 ],
                "searchable": false
            },
            {
                "targets": [ 4 ],
                "visible": false,
				"searchable": true

            }
        ]
      });
		if(dependentsDisplay == 0) {
			$.ajax({
				type: "POST",
				url: "/employees/profile/personal/general",		
				dataType: 'html',
				data: {"get":"EmpDependents"},	
				async:true,
				cache: false,
				beforeSend:function() {
					$('#medloadingImg').removeClass('displayNone');
					$('#medloadingImg').show();
				},
				success: function(response){
					$('#medloadingImg').addClass('displayNone');
					$('#medloadingImg').hide();
					if(response){
							dependentsDisplay = 1;
							$('.dataTable').dataTable();
							$('.empDependents').html(response);  

					}
					else{ 
						$('#successAlert').addClass('clear');
						$('#failAlert').removeClass('clear');
						$('#failedAlertMsg').html("Error while fetchingdata . Please try after sometime.");
						$('#failAlert').fadeIn();
						setTimeout(function(){$('#failAlert').fadeOut("slow");}, 2000); 
					}
				} 
			});
		} 						
	}

