	function displayEcontacts() { 

	var table = $('#tableEcontact').DataTable();
	table.search('').columns().search('').draw();
		if(econtactsDisplay == 0) {
			$.ajax({
				type: "POST",
				url: "/employees/profile/personal/general",		
				dataType: 'html',
				data: {"get":"EmpEContacts"},	
				async:true,
				cache: false,
				beforeSend:function() {
					$('#econLoadingImg').removeClass('displayNone');
					$('#econLoadingImg').show();

				},
				success: function(response){
					$('#econLoadingImg').addClass('displayNone');
					$('#econLoadingImg').hide();
					if(response){
							econtactsDisplay = 1; 
							$('.empContacts').html(response); 
							$('#tableEcontact').dataTable().fnDestroy();
							$('#tableEcontact').dataTable();
					}
					else{ 
						$('#successAlert').addClass('clear');
						$('#failAlert').removeClass('clear');
						$('#failedAlertMsg').html("Error while fetchingdata . Please try after sometime.");
						$('#failAlert').fadeIn();
						setTimeout(function(){$('#failAlert').fadeOut("slow");}, 2000); 
					}
				} 
			});
		} 						
	}

	function displayEducation() { 
		var table = $('.empEducation .dataTable').DataTable();
		table.search('').columns().search('').draw();
		if(educationDisplay == 0) {
			$.ajax({
				type: "POST",
				url: "/employees/profile/personal/general",		  
				dataType: 'html',
				data: {"get":"EmpEducation"},	
				async:true,
				cache: false,
				beforeSend:function() {
					$('#eduloadingImg').removeClass('displayNone');
					$('#eduloadingImg').show();
				},
				success: function(response){
					$('#eduloadingImg').addClass('displayNone');
					$('#eduloadingImg').hide();
					if(response){ 
							educationDisplay = 1; 
							$('.empEducation').html(response); 
							$('.empEducation .dataTable').dataTable();
							$('button.saveEdu').attr({disabled:true});
							$('button.saveEdu').addClass('btn');
							$('button.cancelEdt').attr({disabled:true});
							$('input.editable').attr('disabled', 'disabled');
							$('select.editable').attr('disabled', 'disabled');
					}
					else{ 
						$('#successAlert').addClass('clear');
						$('#failAlert').removeClass('clear');
						$('#failedAlertMsg').html("Error while fetchingdata . Please try after sometime.");
						$('#failAlert').fadeIn();
						setTimeout(function(){$('#failAlert').fadeOut("slow");}, 2000); 
					}
				} 
			});
		} 						
	}


	function displayProjects() { 
		if(projectsDisplay == 0) {
			$.ajax({
				type: "POST",
				url: "/employees/profile/personal/general",		
				dataType: 'html',
				data: {"get":"empProjects"},	
				async:true,
				cache: false,
				beforeSend:function() {
					$('#eduloadingImg').removeClass('displayNone');
					$('#eduloadingImg').show();
				},
				success: function(response){
					$('#eduloadingImg').addClass('displayNone');
					$('#eduloadingImg').hide();
					if(response){ 
							projectsDisplay = 1; 
							$('.empProjects').html(response); 
							$('#tableProjects').dataTable(); 
					}
					else{ 

					}
				} 
			});
		} 						
	}


    $(document).ready(function() {
		if('0' == 1) {
			$('.subProfile').hide();
		}
			$('.dataTable').DataTable();
		<!-- start Expand and collapse all Profile Tabs -->

		$(".expandProfileTabs").on("click", function() {

				$('#generalLoadingImg').removeClass('displayNone');
				$('#generalLoadingImg').css("display", "");
				var numPanelOpen = $('.ProfileTabsPrnt').find('.collapse.in').length;
				$(this).toggleClass("active");

				if (numPanelOpen == 0) {
				  openAllPanels();
				} else {
				  closeAllPanels();
				}

		 });

		openAllPanels = function(aId) {
			//setAllPanelOpen
			if($('.ProfileTabsPrnt').find('.collapse:not(".in")').prev('.profileTab').trigger('click')) {
				$('.expandProfileTabs').text('Collapse All');
				setTimeout(function(){$('#generalLoadingImg').fadeOut("slow");
				 $('#generalLoadingImg').addClass('displayNone');
				}, 1000);	

			} 
		}
		closeAllPanels = function(aId) {
			//setAllPanelclose
			if($('.ProfileTabsPrnt').find('.collapse.in').collapse('hide')) {
				$('.expandProfileTabs').text('Expand All');
				$('.ProfileTabsPrnt').find('.collapse-icon i').removeClass('fa-minus').addClass('fa-plus');
				$('.cancelEdtApproveSkill,.cancelEdtSkill,.cancelEdtMed,.cancelEdtFam,.cancelEdtEdu,.cancelEdtEcon').trigger('click');
				$('#generalLoadingImg').addClass('displayNone');

			}
		}
		<!-- End Expand and collapse all Profile Tabs -->

			$(document).on('click', '.saveApproveSkill', function(event){ 
			$('#skillloadingImg').removeClass('displayNone');
			$('#skillloadingImg').show();
			var data = {};
			data['type'] = $(this).closest('tr').find('#skillType').val();
			data['id'] = $(this).closest('tr').find('.skill_id').val();
			var skillRecords = JSON.stringify(data);

			if(data['type']==''||data['type']==null){
				$(this).closest('tr').find('#skillType').addClass('highlight_item_error');
				$('div#skillValidationMsg').removeClass('clear');
				$('#skillloadingImg').addClass('displayNone');
				$('#skillloadingImg').hide();
				return false;
			}else{
				$(this).closest('tr').find('#skillType').attr('disabled',true);
				$(this).closest('tr').find('#skillType').removeClass('highlight_item_error');
				$('div#skillValidationMsg').addClass('clear');
				$.ajax({
					type: "POST",
					dataType: 'text',
					data: 'empSkillDtls=' + encodeURIComponent(skillRecords),	
					url: "/employees/profile/personal/general?empId="+empId,
					cache: false,
					success: function(response){
						$('#skillloadingImg').addClass('displayNone');
						$('#skillloadingImg').hide();
						if(response){
							$("#skillSec").html(response); 
							$('button.saveApproveSkill, button.cancelEdtApproveSkill, button#cancelBtn').button({disabled:true});
							$('button.editApproveSkill, button.editSkill, button#addSkill, button.delSkill').button({disabled:false}); 
							$('div#skillValidationMsg').addClass('clear');
							// $('#tableEmpSkill').dataTable().fnDestroy();
							// $('#tableEmpSkill').dataTable({"bSort": false});
							$('#skillSuccessAlert').removeClass('clear');
							$('#skillSuccessAlertMsg').html("Skill details saved successfully.");
							$('#skillFailAlert').addClass('clear');
							$('#skillSuccessAlert').fadeIn();
							setTimeout(function(){$('#skillSuccessAlert').fadeOut("slow");}, 2000);	
						}else{
							$('#skillSuccessAlert').addClass('clear');
							$('#skillFailAlert').removeClass('clear');
							$('skillFailAlertMsg').html("Error saving skills. Please try after sometime.");
							$('#skillFailAlert').fadeIn();
							setTimeout(function(){$('#skillFailAlert').fadeOut("slow");}, 2000);
						}
						recalcTabs('Skill');
					}
				});
			}
		});


