		$('div.delFamDlg').dialog({
			autoOpen: false,
			modal: true,
			buttons: {
				'Yes': function() { 
							//if(curTR != null){
								$('#famLoadingImg').removeClass('displayNone');
								$('#famLoadingImg').show();
