								// AJAX Code To Submit Form.
										$.ajax({
										type: "POST",
										url: "/employees/profile/personal/general?"+ajaxURL,		
										async:true,
										cache: false,
										success: function(response){

											$('#famLoadingImg').addClass('displayNone');
											$('#famLoadingImg').hide();
												if(response){
													$('#fd').html(response);												
													$('input.editable').attr('disabled', 'disabled');
													$('select.editable').attr('disabled', 'disabled');
													$("button.saveFam").button({disabled:true});
													$('#familySuccessAlert').removeClass('clear');
													$('#familySuccessAlertMsg').html("Family details deleted successfully.");
													$('#failAlert').addClass('clear');
													$('#familySuccessAlert').fadeIn();
													setTimeout(function(){$('#successAlert').fadeOut("slow");}, 2000);
													curTR.remove();
													curTR.find('input[name*=is_active]').val('0');
													curTR.attr('style', 'display:none;height:0;');
													curTR.addClass('invisible');
													recalcTabs('Family');
													if($('#familyRelations').find('tr').length == 1){
														if($('#familyRelations').find('tr').has('td.dataTables_empty').hasClass('clear')){
															$('#familyRelations').find('tr').find('td.dataTables_empty').parent('tr').removeClass('clear');
														}
													}
													$('#tableFamilyRel').dataTable().fnDestroy();
													$('#tableFamilyRel').dataTable();

													setTimeout(function(){$('#familySuccessAlert').fadeOut("slow");}, 2000);
													return true;	
												}
											else{

												$('#familySuccessAlert').addClass('clear');
												$('#familyFailAlert').removeClass('clear');
												$('#familyFailAlertMsg').html("Error while deleting family details . Please try after sometime.");
												$('#familyFailAlert').fadeIn();
												setTimeout(function(){$('#familyFailAlert').fadeOut("slow");}, 2000);

												}
											} 
										});
							},
							'No' : function() {	
								$(this).dialog('close');
								}



			},
			open: function(event, ui) {
				var dialog = $(event.target).parents(".ui-dialog.ui-widget");
				var buttons = dialog.find(".ui-dialog-buttonpane").find("button");
				$(buttons[0]).addClass("btn btn-owncolor");
				$(buttons[1]).addClass("btn btn-default");
				}


		});

		$(document).on('click','button.delFam',function(){    	
			curTR = $(this).closest('tr'); 			
			var famDtlsId = $(this).attr('id');	

			var sectionName = $(this).closest('tr').find('input#familySection').val();	
			var dtls = "famId="+famDtlsId+"&sectionName="+encodeURIComponent(sectionName);
			$('#famAjaxUrl').val(dtls);
			$('div.delFamDlg').dialog('open');
