		});
	//add certificate details

		$(document).on('click','.addCert',function(){ 
			$(this).button({disabled:true});
			$("tbody#ceritificateRecords button.delCert").button({disabled:true});
			$("tbody#ceritificateRecords button.editCert").button({disabled:true});					
			var certSerial = 0;  	
				certSerial++;
				recalcTabs('Certification');
				var items_cert= [];
				var rows_cert= [];
				$('#tableCertifications tbody tr.itemCertification').each( function(){
					rows_cert.push(''); 
				});

				$('#tableCertifications tbody td.serialCount').each( function(){
					items_cert.push($(this).text()); 
				});

				if(items_cert.length >0){
					var sno=Math.max.apply(Math,items_cert) + 1;
				}else if(rows_cert.length ==0){
					var sno=1;
				}else{
					var sno=certSerial;
				}

				$('#certifyTpl').find("td:first").html(sno);
					$("#certifyTpl button.saveCert").button({disabled:false});	
				if($('#ceritificateRecords').find('tr').length == 1){
					if(!$('#ceritificateRecords').find('tr').has('td.dataTables_empty').hasClass('clear')){
						$('#ceritificateRecords').find('tr').find('td.dataTables_empty').parent('tr').addClass('clear');
					}
				}				  
				var tpl = $('#certifyTpl').html(); 
				tpl = tpl.replace("__data__", sno);	
				tpl = tpl.replace("__rec__", sno);	
				tpl = tpl.replace("__trCountCertification__", sno + " itemCertification");	
				$('#ceritificateRecords').append(tpl);	
				$(".datepicker").removeClass('hasDatepicker');
		}); 
		$(document).on('click', '.saveCert', function(event){ 
			var currentElem = this;   
			var isError = false;
			var validation = false;
			var alphabets = /^([a-zA-Z .]{3,50})$/;
			var tmp = {};
			$("tbody#ceritificateRecords button.addCert").button({disabled:true});
			$(this).closest('tr').find('td:has(.validate)').each(function() {
					var textBox = $(this).find('.validate');
					var val = textBox.val().trim();
					var extCert=$(this).closest('tr').find('.extCert').val().trim();
					if($('#certType').val()==2){
						if(extCert.length==0){
							$(this).closest('tr').find('.extCert').addClass('highlight_item_error');
							isError = true;	
						}else{
							$(this).closest('tr').find('.extCert').removeClass('highlight_item_error');
						}
					}
					if(textBox.hasClass('userName')){
							if(!val.match(alphabets)){
							validation = true;
						}
					}
				if(val.length == 0 || validation == true){
						textBox.addClass('highlight_item_error');
							isError = true;				
					}
				else{
					textBox.removeClass('highlight_item_error');
					}
				});		
			if(isError) {
				$('div#certValidationMsg').removeClass('clear');
					// $("body, html").animate({
						// scrollTop: $("#certificateSec").position().top + 200
					// });
				return false;
			} else {

			$(this).closest('tr').find('td:has(input)').each(function() {
					var row = $(this);
					var values = "";
					tmp[$(this).find('input').attr('name')] = $(this).find('input').val();
			});
					var empCertDtls = JSON.stringify(tmp); 
					$('#certloadingImg').removeClass('displayNone');
					$('#certloadingImg').show();
					var myCurrnt=$(this);
					$.ajax({
						type: "POST",
						dataType: 'html',
						data: 'empCertDtls=' + encodeURIComponent(empCertDtls)+'&isAdmin=0',	
						url: "/employeesAdmin/profile/personal/general?empId="+empId,	
						async: true,
						cache: false,
						success: function(response){
								$('#certloadingImg').addClass('displayNone');
								$('#certloadingImg').hide();	
					if(response==-1){
						$('#certSuccessAlert').addClass('clear');
						$(currentElem).closest('tr').find('.name').addClass('highlight_item_error');
						$('#certFailAlert').removeClass('clear');
						$('#certFailAlertMsg').html("Certification Name already exists...");
						$('#certFailAlert').fadeIn();
						setTimeout(function(){$('#certFailAlert').fadeOut("slow");}, 2000);
					}else{									 
						if(response){
							$("#certificateSec").html(response); 						
							$('button.saveCert, button.cancelEdtCert').button({disabled:true});	
							$('input.editable').attr('disabled', 'disabled');
							$('select.editable').attr('disabled', 'disabled');  
							$('div#certValidationMsg').addClass('clear');
							$('#tableCertifications').dataTable().fnDestroy();
							$('#tableCertifications').dataTable();
							$('#certSuccessAlert').removeClass('clear');
							$('#certSuccessAlertMsg').html("Certification details saved successfully.");
							$('#certFailAlert').addClass('clear');						
							$('#certSuccessAlert').fadeIn();
							setTimeout(function(){$('#certSuccessAlert').fadeOut("slow");}, 2000);										
							}
						else{
							$('#certSuccessAlert').addClass('clear');
							$('#certFailAlert').removeClass('clear');
							$('#certFailAlertMsg').html("Error saving certification details . Please try after sometime.");
							$('#certFailAlert').fadeIn();
							setTimeout(function(){$('#certFailAlert').fadeOut("slow");}, 2000);

							}
					}
					}
				});
			}		


		});

