		$(document).on('click','#addSkill',function(){
			//start rating	
			var info = $('#tableEmpSkill').DataTable().page.info();
			var pagenum = info.page; //get page number in pagination
			$("tbody#skillRecords button#cancelBtn").button({disabled:false});
			$("tbody#skillRecords button.delSkill").button({disabled:true});
			$("tbody#skillRecords button.editSkill,button.editApproveSkill").button({disabled:true});				
			var skillSerial = 16;
			skillSerial++;
			var items_skill= [];
			var rows_skill= [];
			$('#tableEmpSkill tbody tr.itemSkill').each( function(){
				rows_skill.push(''); 
			});

			$('#tableEmpSkill tbody td.serialCount').each( function(){
				items_skill.push($(this).text()); 
			});

			if(items_skill.length >0 ){
				var sno=Math.max.apply(Math,items_skill) + 1;
			}else if(rows_skill.length ==0){
				var sno=1;
			}else{
				var sno=skillSerial;
			}
			$('#skillTpl').find("td:first").html(sno);
			$("#skillTpl button.saveSkill").button({disabled:false});
