				}
			}

			var tpl = $('#skillTpl').html(); 
			tpl = tpl.replace("__data__", sno);	
			tpl = tpl.replace("__data__input", sno+"_input");
			tpl = tpl.replace("__trCountSkills__", sno + " itemSkill");	
			$('#skillRecords').append(tpl);	
			$(".datepicker").removeClass('hasDatepicker');
				var i = 1;	
