					var $s2input = $('#star'+i);
					$('.starrr').starrr({
					max: 5,
					rating: $s2input.val(),
					change: function(e, value){
						$(this).next('input').val(value);
					}
					});
					i++;
				});
			$('#addSkill').attr('disabled',true);
			$("button#cancelBtn").button({disabled:false});
		}); 


