				$(this).closest('tr').find('input.editable').removeAttr('readonly');
			});

			/*SAVE EDUCATION DETAILS*/
			$(document).on('click', '.saveEdu', function(event){ 
			var currentElem = this;   
			var isError = false;
			var validation = false;
			var alphabets = /^([a-zA-Z .]{1,50})$/;
			var tmp = {};		
			$(this).closest('tr').find('td:has(.validate)').each(function() {
					var textBox = $(this).find('.validate');
					var val = textBox.val().trim();				
					if(textBox.hasClass('userName')){
							if(!val.match(alphabets)){
							validation = true;
							}
					}
				if(val.length == 0 || validation == true){
						textBox.addClass('highlight_item_error');
							isError = true;				
					}
				else{
					textBox.removeClass('highlight_item_error');
					}
				});		
			if(isError) {
				$('div#eduValidationMsg').removeClass('clear');
				return false;
			} else {

			$(this).closest('tr').find('td:has(input)').each(function() {
				var row = $(this);
				var values = "";
				tmp[$(this).find('input').attr('name')] = $(this).find('input').val();
			});
				var eduRecords = JSON.stringify(tmp); 
				$('#eduloadingImg').removeClass('displayNone');
				$('#eduloadingImg').show();
					var myCurrnt=$(this);
					$.ajax({
						type: "POST",
						dataType: 'html',
						data: 'empEduDtls=' + encodeURIComponent(eduRecords),	
						url: "/employees/profile/personal/general?empId="+empId,	
						async: true,
						cache: false,
						success: function(response){
							$('#eduloadingImg').addClass('displayNone');
							$('#eduloadingImg').hide();		
						if(response){						
							$("#eduSec").html(response); 
							$('input.editable').attr('disabled', 'disabled');
							$('select.editable').attr('disabled', 'disabled');  
							$('div#eduValidationMsg').addClass('clear');
							$('#tableEducation').dataTable().fnDestroy();
							$('#tableEducation').dataTable();
							$('#eduSuccessAlert').removeClass('clear');
							$('#eduSuccessAlertMsg').html("Education details saved successfully.");
							$("button#addEdu").button({disabled:false});
							educationDisplay = 0;
							displayEducation();
							$("button#cancelBtn").button({disabled:true});
							$("button.saveEdu").button({disabled:true});
							$('#eduFailAlert').addClass('clear');
							$('#eduSuccessAlert').fadeIn();
							setTimeout(function(){$('#eduSuccessAlert').fadeOut("slow");}, 2000);										
							}
						else{
							$('#eduSuccessAlert').addClass('clear');
							$('#eduFailAlert').removeClass('clear');
							$('eduFailAlertMsg').html("Error saving education details. Please try after sometime.");
							$('#eduFailAlert').fadeIn();
							setTimeout(function(){$('#eduFailAlert').fadeOut("slow");}, 2000);

							}

						}
				});
		}


		});

