		var empId = 10766;
		$(document).on('click', '.saveMed', function(event){ 
			var currentElem = this;   
			var isError = false;
			var validation = false;
			$('#medFailAlertMsg').html('');
			$('#medSuccessAlertMsg').html('');
			var tmp = {};		
			$(this).closest('tr').find('td:has(.validate)').each(function() {
					var textBox = $(this).find('.validate');
					var val = textBox.val().trim();				

				if(val.length == 0){
						$(this).find('input').addClass('highlight_item_error');
						$(this).find('select').addClass('highlight_item_error');
						isError = true;				
					}
				else{
						$(this).find('input').removeClass('highlight_item_error');
						$(this).find('select').removeClass('highlight_item_error');
					}
				});		
			if(isError) {
				$('div#medicalValidationMsg').removeClass('clear');
				return false;
			} else { 
				$(this).closest('tr').find('td:has(input)').each(function() {
					var row = $(this);
					var values = "";
					tmp[$(this).find('input').attr('name')] = $(this).find('input').val();
				});
				$(this).closest('tr').find('td:has(select)').each(function() {
					var row = $(this);
					var values = "";
					tmp[$(this).find('select').attr('name')] = $(this).find('select').val();

				});
				var medicalRecords = JSON.stringify(tmp);

				$('#medloadingImg').removeClass('displayNone');
					$('#medloadingImg').show();
					$('#medSuccessAlertMsg').html('');
					var myCurrnt=$(this);
					saveMedClaims(medicalRecords);
					return false;
			} 

		});
		function saveMedClaims(medicalRecords) {
			$.ajax({
						type: "POST",
						dataType: 'json',
						data: 'MediclaimDtls=' + encodeURIComponent(medicalRecords),	
						url: "/employees/profile/personal/general?empId="+empId,	
						async: false,
						cache: false,
						success: function(response){ 
							$('#medloadingImg').addClass('displayNone');
							$('#medloadingImg').hide(); 
						if(response.status){
							$('button.cancelEdtMed').button({disabled:true});	
							$('input.editable, select.editable').attr('disabled', 'disabled');
							$('input.editable, select.editable').attr('readonly', 'readonly');	
							$('div#medicalValidationMsg').addClass('clear');
							$('#medSuccessAlert').removeClass('clear');
							$('#medSuccessAlertMsg').html(response.message);
							$('#medFailAlert').addClass('clear');
							$('#medSuccessAlert').fadeIn();
							setTimeout(function(){$('#medSuccessAlert').fadeOut("slow");$('#medSuccessAlert').hide();	}, 2000);
							loadDependants();						
							return false;
							}
						else{
							$('#medSuccessAlert').addClass('clear');
							$('#medFailAlert').removeClass('clear');
							$('#medFailAlertMsg').html(response.message);
							$('#medFailAlert').fadeIn();
							setTimeout(function(){$('#medFailAlert').fadeOut("slow");}, 2000); 
							}
						}
				});
				return false;
		}
