		$(document).on('click', '.saveFam', function(event){ 
					event.preventDefault();
					var currentElem = this;   
					var isError = false;
					var validation = false;
					var alphabets = /^([a-zA-Z .]{3,50})$/;
					var tmp = {};
					var notUnique = false;
					var nameTxtbx = $(this).closest('tr').find('input.userName');
					var newNameVal = $(this).closest('tr').find('input.userName').val();
					//getting records to save
						$(this).closest('tr').find('td:has(input)').each(function() {
						var row = $(this);
						var values = "";
						tmp[$(this).find('input').attr('name')] = $(this).find('input').val();
						tmp.newName = newNameVal;
						});
						var familyRecords = JSON.stringify(tmp); 

					$(this).closest('tr').find('td:has(.validate)').each(function() {
							var textBox = $(this).find('.validate');
							var val = textBox.val().trim();				
							if(val.length == 0){
								$(this).find('input').addClass('highlight_item_error');
								$(this).find('select').addClass('highlight_item_error');
									isError = true;				
							}
						else{
							$(this).find('input').removeClass('highlight_item_error');
							$(this).find('select').removeClass('highlight_item_error');
							}
						});	

						$.ajax({
								type: "POST",
								dataType: 'html',
								data: 'empId='+empId+'&famDtls=' + encodeURIComponent(familyRecords),	
								url: "/employeesAdmin/checkfamilyname",	
								async: false,
								cache: false,
								success: function(response){

								if(response == true){
									notUnique = true;
									}
								else{
									notUnique = false;
									}
								}
						});	

					if(isError) {
						$('div#familyValidationMsg').removeClass('clear');
						return false;
					}else if(notUnique){
						$('div#familyValidationMsg').removeClass('clear');
						$(nameTxtbx).val('');
						$(nameTxtbx).addClass('highlight_item_error');			
						$('#familySuccessAlert').addClass('clear');
						$('#familyFailAlert').removeClass('clear');
						$('#familyFailAlertMsg').html("This name is already exist.Please write another name.");
						$('#familyFailAlert').fadeIn();
						setTimeout(function(){$('#familyFailAlert').fadeOut("slow");}, 2000);

					} else {

						$('#famLoadingImg').removeClass('displayNone');
							$('#famLoadingImg').show();
							var myCurrnt=$(this);
							$.ajax({
								type: "POST",
								dataType: 'html',
								data: 'FamilyDtls=' + encodeURIComponent(familyRecords),	
								url: "/employees/profile/personal/general",	
								async: true,
								cache: false,
								success: function(response){
									$('#famLoadingImg').addClass('displayNone');
									$('#famLoadingImg').hide();						
								if(response){
									$("#fd").html(response); 
									$("button.cancelEdtFam").button({disabled:true});
									$('input.editable').attr('disabled', 'disabled');
									$('select.editable').attr('disabled', 'disabled');  						
									$('div#familyValidationMsg').addClass('clear');
									$('#familySuccessAlert').removeClass('clear');
									$('#familySuccessAlertMsg').html("Family details saved successfully.");
									$('#familyFailAlert').addClass('clear');
									$('#familySuccessAlert').fadeIn();
									setTimeout(function(){$('#familySuccessAlert').fadeOut("slow");}, 2000);										
									}
								else{
									$('#familySuccessAlert').addClass('clear');
									$('#familyFailAlert').removeClass('clear');
									$('#familyFailAlertMsg').html("Error saving family details . Please try after sometime.");
									$('#familyFailAlert').fadeIn();
									setTimeout(function(){$('#familyFailAlert').fadeOut("slow");}, 2000);

									}
									$('#tableFamilyRel').dataTable().fnDestroy();
									$('#tableFamilyRel').dataTable();
								}
						});
				}


		});


