		$(document).on('click', '.saveSkill', function(event){ 
			event.preventDefault();
			var currentElem = this;   
			var isError = false;
			var notUnique ;
			var validation = false;
			var alphabets = /^([a-zA-Z .]{1,50})$/;
			var tmp = {};	
			var nameTxtbx = $(this).closest('tr').find('input.skillName');
			var newName = $(this).closest('tr').find('input.skillName').val();
			$(this).closest('tr').find('td.skillname').find('input').attr('name','skillname');
			$(this).closest('tr').find('td:has(input)').each(function() {
				var row = $(this);
				var values = "";
				tmp[$(this).find('input').attr('name')] = $(this).find('input').val();
			} );
			$(this).closest('tr').find('td:has(select)').each(function() {
				tmp[$(this).find('select').attr('id')] = $(this).find('select').val();
			} );
			if(tmp['skillId']==''){
				var sklid= $(this).closest('tr').find('#skillId').prev().prev().html();
				if(sklid!=null){
					tmp['skillId']=sklid;
				}
			}

			var skillRecords = JSON.stringify(tmp);
			$.ajax({
				type: "POST",
				dataType: 'html',
				data: 'skillDtls=' + encodeURIComponent(skillRecords) ,	
				url: "/employees/profile/personal/general?empId="+empId,	
				async: false,
				cache: false,
				success: function(response){
					$('#skillloadingImg').addClass('displayNone');
					$('#skillloadingImg').hide();						
					if(response == true){
						notUnique = true;
						}
					else{
						notUnique = false;
					}
				}
			});  
			if(tmp['skillGroupID']==''  || tmp['skillGroupID']== null){
				isError=true;
				$(this).closest('tr').find('#skillGroupID').addClass('highlight_item_error');
			}else{
				$(this).closest('tr').find('#skillGroupID').removeClass('highlight_item_error');
			}
			if(tmp['skillId']=='' || tmp['skillId']== null || tmp['skillId']=="<< Please Select >>"){
				isError=true;
				$(this).closest('tr').find('#skillId').addClass('highlight_item_error');
				$(this).closest('tr').find( "input[name='skillname']").addClass('highlight_item_error');
			}else{
				$(this).closest('tr').find('#skillId').removeClass('highlight_item_error');
				$(this).closest('tr').find( "input[name='skillname']").removeClass('highlight_item_error');
			}
			if(tmp['skillExp'] == 0 || tmp['skillExp']=='' || tmp['skillExp']== null ){
				isError=true;
				$(this).closest('tr').find('input#skillExp').addClass('highlight_item_error');
			}else{
				$(this).closest('tr').find('input#skillExp').removeClass('highlight_item_error');
			}
			$(this).closest('tr').find('td:has(.validate)').each(function() {
				var textBox = $(this).find('.validate');
				var val = textBox.val().trim();
				if(val.length == 0){
					$(this).find('div.starrr:has(i)').each(function(){
						$(this).find('i').each(function(){	
							$(this).css("color", "red");
						});
				  });
					$(this).find('input').addClass('highlight_item_error');
					$(this).find('select').addClass('highlight_item_error');
					isError = true;									
				}else{
					$(this).find('input').removeClass('highlight_item_error');
					$(this).find('select').removeClass('highlight_item_error');
					}
			});	
				if(isError) {
					$('div#skillValidationMsg').removeClass('clear');
					return false;
				}else if(notUnique){	
					$('div#skillValidationMsg').addClass('clear');	
					$(this).closest('tr').find('#skillGroupID').addClass('highlight_item_error');			
					$(this).closest('tr').find('#skillId').addClass('highlight_item_error');
					$(this).closest('tr').find( "input[name='skillname']").addClass('highlight_item_error');		
					$('div#skillSuccessAlert').addClass('clear');
					$('div#skillFailAlert').removeClass('clear');
					$('strong#skillFailAlertMsg').html("This Skill is already exist. Please submit another skill.");
					$('div#skillFailAlert').fadeIn();
					setTimeout(function(){$('div#skillFailAlert').fadeOut("slow");}, 2000);
					return false;			
				}else{	
					// $(this).closest('tr').find('#skillId').find('option').remove();	
					$('#skillloadingImg').removeClass('displayNone');
					$('#skillloadingImg').show();
					var myCurrnt=$(this);
					$.ajax({
						type: "POST",
						dataType: 'html',
						data: 'empSkillDtls=' + encodeURIComponent(skillRecords),	
						url: "/employees/profile/personal/general?empId="+empId,	
						async: true,
						cache: false,
						success: function(response){
							$('#skillloadingImg').addClass('displayNone');
							$('#skillloadingImg').hide();		
						if(response){

							$("#skillSec").html(response); 
							$('button.saveSkill, button.cancelEdtSkill').button({disabled:true});
							$('input.editable').attr('disabled', 'disabled');
							$('select.editable').attr('disabled', 'disabled');  
							$('div#skillValidationMsg').addClass('clear');
							// $('#tableEmpSkill').dataTable().fnDestroy();
							// $('#tableEmpSkill').dataTable({"bSort": false});
							$('#skillSuccessAlert').removeClass('clear');
							$('#skillSuccessAlertMsg').html("Skill details saved successfully.");
							$('#skillFailAlert').addClass('clear');
							$('#skillSuccessAlert').fadeIn();
							setTimeout(function(){$('#skillSuccessAlert').fadeOut("slow");}, 2000);										
							}
						else{
							$('#skillSuccessAlert').addClass('clear');
							$('#skillFailAlert').removeClass('clear');
							$('skillFailAlertMsg').html("Error saving skills. Please try after sometime.");
							$('#skillFailAlert').fadeIn();
							setTimeout(function(){$('#skillFailAlert').fadeOut("slow");}, 2000);

							}
							$(this).closest('tr').find('.starEdit').replaceWith($('div.starContainer'));
							recalcTabs('Skill');
						}
					});

		 		}	
				return false;
			});



