		$(document).on('click','#addFam',function(){ 
			var serial = 26;				
			serial++;

			recalcTabs('Family');
			var items= [];
			var rows_family= [];
			//var sno=parseInt($('#familyRelations').find("tr:last td.SNo-width").html()) + 1;
			$i=0;
			$('#tableFamilyRel tbody td.serialCount').each( function(){
				$i++;   //add item to array
				items.push($(this).text());      
			});

			$('#tableEcontact tbody tr.itemFamily').each( function(){
				rows_family.push('');      
			});

			if(items.length > 0){
				var sno=Math.max.apply(Math,items) + 1;
			}else if(rows_family.length ==0){
				var sno=1;
			}else{
				var sno=serial;
			}

			$('#familyTpl').find("td:first").html(sno);
			$("#familyTpl button.saveFam").button({disabled:false});				
			var tpl = $('#familyTpl').html(); 
			tpl = tpl.replace("__data__", sno);	
			tpl = tpl.replace("__datepicker__", sno);	
			tpl = tpl.replace("__trCountFamily__", sno + " itemFamily");

			if($('#familyRelations').find('tr').length == 1){
				if(!$('#familyRelations').find('tr').has('td.dataTables_empty').hasClass('clear')){
					$('#familyRelations').find('tr').find('td.dataTables_empty').parent('tr').addClass('clear');
				}
			}

			$('#familyRelations').append(tpl);
			$("tbody#familyRelations button.delFam").button({disabled:true});
			$("tbody#familyRelations button.editFam").button({disabled:true});						
			$(".datepicker").removeClass('hasDatepicker');
			$('#addFam').attr('disabled',true);

		});      
