			$("tbody#econtacts button.delBtn").button({disabled:true});
			$("tbody#econtacts button.edtBtn").button({disabled:true});	

			var count = 0;

			$('button.saveBtn, button.edtBtn, button#addBtn').button();

			count++;

			recalcTabs('Emergency');

			var items_econtact= [];
			var rows_econtact= [];

			$('#tableEcontact tbody tr.itemEmergency').each( function(){
				rows_econtact.push('');      
			});

			$('#tableEcontact tbody td.serialCount').each( function(){
				items_econtact.push($(this).text());      
			});

			if(items_econtact.length >0){
				var sno=Math.max.apply(Math,items_econtact) + 1;
			}else if(rows_econtact.length ==0){
				var sno=1;
			}else{
				var sno=count;
			}

			$('#contactTpl').find("td:first").html(sno);
			$('#contactTpl').find('button.saveBtn').button('option', 'disabled', false);
			$('#contactTpl').find('button.edtBtn').button('option', 'disabled', true);
			var tpl = $('#contactTpl').html();	
			tpl = tpl.replace("__trCountEmergency__", sno + " itemEmergency");		

			if($('#econtacts').find('tr').length == 1){
				if(!$('#econtacts').find('tr').has('td.dataTables_empty').hasClass('clear')){
					$('#econtacts').find('tr').find('td.dataTables_empty').parent('tr').addClass('clear');
				}
			}

			$('#econtacts').append(tpl);

		});



