							}

							$("#work_window").html(wwHtml);
						}					
						var supName = EmpDetails.Manager.first_name+' '+EmpDetails.Manager.middle_name+' '+EmpDetails.Manager.last_name;
						$("#sup_name").html(supName?supName:'N/A');
