	//image uploadtext
    function readURL(input) {
      var imgExtVal =  fileExtValidate(input);
	  var imgSizeVal = fileSizeValidate(input);
		if(imgExtVal == false || imgSizeVal == false){
		$('#errMsgImg').removeClass('clear');
		$('div.imagDiv').removeClass('clear');
		$("button.btnSaveMain").attr('disabled','disabled');
		return false;

		}else{
		$("button.btnSaveMain").removeAttr('disabled');
		$('#errMsgImg').addClass('clear');
		$('div.imagDiv').addClass('clear');
		if (input.files && input.files[0]) {
				var reader = new FileReader();

				reader.onload = function(e) {
					$('#selectedImg').attr('src', e.target.result).width(150).height(150);
					$('.avatar-bg').css('background-image', 'url(' + e.target.result + ')');

				};

				reader.readAsDataURL(input.files[0]);
			}
		}
	}	
    //extension validation for profile img		
	function fileExtValidate(fdata) {
		var validExt = ".jpeg, .jpg";
		var filePath = fdata.value;
		var getFileExt = filePath.substring(filePath.lastIndexOf('.') + 1).toLowerCase();
		var pos = validExt.indexOf(getFileExt);
		if(pos < 0) {
			return false;
		} else {
			return true;
		}
	}
   	//profile image size validation


function fileSizeValidate(fdata) {
	var maxSize = '1024';
	 if (fdata.files && fdata.files[0]) {
		var fsize = fdata.files[0].size/1024;
		if(fsize > maxSize) {
			return false;
		} else {
			return true;
		}
     }
 }  


	function resign(){

		$('div.ResgMsgDlg').dialog('close');	
		var notes = $("#exitReason").val().trim();
		$.ajax({
			type: "POST",
			dataType: 'html',
			data: 'req_notes=' + encodeURIComponent(notes),	
			url: '/employees/profile/employment/resignation?action=submit',	
			async: true,
			success: function(response){			
				if(response){
					var responseObj = JSON.parse(response);
					$('#resignSuccessMsg').html("Your Resignation request is under Heat Move Phase, it will take 24hrs to move to resignation state till then you will have option to withdraw.");
					$('button#createExit').text('Resignation applied');							
					$('div#familyValidationMsg').addClass('clear');
					$('#resignSuccess').removeClass('clear');
					$('#resignFailMsg').addClass('clear');
					$('#resignSuccess').fadeIn();
					setTimeout(function(){$('#resignSuccess').fadeOut("slow");}, 2000);					
					//assign value to the element
					if(responseObj.activeExit.EmpExitReview.length > 0){
						$("#reviewHist").removeClass('clear');
						$.each(responseObj.activeExit.EmpExitReview, function(i, item) {
							$(".reviewTbl").append('<tr><td>'+responseObj.activeExit.EmpExitReview[i].Reviewer.first_name+' '+responseObj.activeExit.EmpExitReview[i].Reviewer.middle_name+' '+responseObj.activeExit.EmpExitReview[i].Reviewer.last_name+' </td><td> '+(responseObj.activeExit.EmpExitReview[i].is_cancelled == true ?'Cancelled':(responseObj.activeExit.EmpExitReview[i].is_completed == true ? responseObj.activeExit.EmpExitReview[i].review_dt:'Pending Review'))+'</td><td><div class=""><label></label><div class="ellipsis-data" id="empViewNote">'+(responseObj.activeExit.EmpExitReview[i].is_completed == true ? responseObj.activeExit.EmpExitReview[i].emp_view_notes : 'Not Available')+'</div></div></td></tr>');
						});
					}

					if(!$.isEmptyObject(responseObj.actions)){				
						var actionList = '<select></select>';
						$.each(responseObj.actions, function(key, item) {
							var option = $('<option />').val(key).text(item);										
							$("#nextAction").append(option);
						 });
						$("#listOfActions").removeClass('clear');			
						$("#noActions").addClass('clear');										
					}else{
						$("#listOfActions").addClass('clear');	
						$("#actionsList").html('No actions available Currently.');

					}
					$("#exitId").val(responseObj.activeExit.EmpExit.id);
					$("#resignDt").val(formatDate(new Date(responseObj.activeExit.EmpExit.create_dt))); 
					$("#resignStatus").val(responseObj.activeExit.EmpExit.status);
					$("#reqNote").html(responseObj.activeExit.EmpExit.req_notes);
					$("#cancelNote").val(responseObj.activeExit.EmpExit.cancel_notes);
					$("#afterResign").removeClass('clear');
					$('#beforeResign').addClass('clear');
					$('.resignYesBtn').removeAttr('disabled');								
					$('.beforeResMsg').hide();		
					// $('div.ResgMsgDlg').dialog('close');	

				}else{
					$('#resignSuccess').addClass('clear');
					$('#familyFailAlert').removeClass('clear');
					$('#resignFailMsg').html("Error occurred. Please try after sometime.");
					$('#familyFailAlert').fadeIn();
					setTimeout(function(){$('#familyFailAlert').fadeOut("slow");}, 3000);
				}

				$('#resignSpinner').addClass('displayNone');
				$('#resignSpinner').hide();	
			}
		});

}
//Reset serial no in family details on deleting row
	function recalcId(){

		$.each($("table tr.item"),function (i,el){
			$(this).find("td:first").html(i + 1); 
			$(this).removeAttr('class'); 
			$(this).addClass('prototype '+i+' item'); 
		})

	}
	//Reset serial no in Emergency Contact details on deleting row
	function recalcTabs(tab){

		$.each($("table tr.item"+tab),function (i,el){

			$(this).find("td.serialCount").html(i+1); 
			$(this).removeAttr('class'); 
			$(this).addClass((i+1)+' item'+tab); 
		});

	}
	   //emergency contact ended
    function tempDelete(e,tab){
		var tbodyId=$("tr.item"+tab).parent('tbody').attr('id');
		$(this).filter("button.newSkillDel").attr('disabled', false);
		if($('#'+tbodyId).find('tr').length == 2){
			if($('#'+tbodyId).find('tr').has('td.dataTables_empty').hasClass('clear')){
				$('#'+tbodyId).find('tr').find('td.dataTables_empty').parent('tr').removeClass('clear');
			}
		}
	    //e.siblings('#cancelBtn').removeAttr('disabled');
		// e.closest('tr').remove();
		$(e).closest('tr').remove();
		recalcTabs(tab);
		return false;
    }/**/

