$(".attLoginModal").click(function(){
	$('#loadingImgAtt').removeClass('displayNone');
	$('#loadingImgAtt').show();
	$('#attFailAlert').hide();
	$(".attLoginBtn,.attLogoutBtn").show();
	$.ajax({
		type: "POST",
		dataType: 'html',
		data :  'read='+1,
		url: "/employees/EmpLoginAttTrack",	
		success: function(response){ 

			if(response.length > 1) {

				$('.loginPrnt,.logoutPrnt').show();
				if(JSON.parse(response).logins != 0) {
					$('.attlogin').html(JSON.parse(response).logins.login);
					$('.attlogout').html(JSON.parse(response).logins.logout);
				} else {
					$('.attlogin').html('-');
					$('.attlogout').html('-');
				}
				if(JSON.parse(response).logins.login) {
					$('.attLoginBtn').hide();
					$('.attLogoutBtn').show();
				} else {
					$('.attLogoutBtn').hide();
				}

				if(JSON.parse(response).message != 1) {
					$('#attFailAlertMsg').html(JSON.parse(response).message);
					$('#attFailAlert').removeClass('clear').show();

				} else {
					$('.loginBtns').show();
				}

				if(JSON.parse(response).enableLogin == 0) {
					$('.loginBtns').hide();
					$('.loginPrnt,.logoutPrnt').hide();
				}
				if(JSON.parse(response).enableLogout == 1) {
					$('.loginBtns').show();
					$('.attLogoutBtn').show();
				}

			}
			$('#attLoginModal').modal('show');
			$('#loadingImgAtt').addClass('displayNone');
			$('#loadingImgAtt').hide();
		} 
	}); 
});

$(".attLoginBtn,.attLogoutBtn").click(function(){

	$('#loadingImgAtt').removeClass('displayNone');
	$('#loadingImgAtt').show();
	$('#attFailAlert').hide();
	$('#attSuccessAlert').hide();
	var isLogin = 0;
	var btn = $(this);
	if($(btn).attr('value') == 'login') {
		isLogin = 1;
	}
	$.ajax({
		type: "POST",
		dataType: 'html',
		data :  'isLogin='+isLogin,
		url: "/employees/EmpLoginAttTrack",	
		success: function(response){ 
			console.log(response);
			if(response.length > 1) {
				$('.loginPrnt,.logoutPrnt').show();
				if(JSON.parse(response).logins != 0) {
					$('.attlogin').html(JSON.parse(response).logins.login);
					if(JSON.parse(response).logins.logout != null) {
						$('.attlogout').html(JSON.parse(response).logins.logout);
					}
				} else {
					$('.attlogin').html('--');
					$('.attlogout').html('--');
				}
				if(JSON.parse(response).logins.login) {
					$('.attLoginBtn').hide();
				}

				if(JSON.parse(response).success == 1) {
					$('#attSuccessAlertMsg').html('Attendance Recorded successfully.');
					$('#attSuccessAlert').removeClass('clear').show();
					setTimeout(function(){
						$('#attSuccessAlert').fadeOut("slow");}, 2000);
				}

				if(JSON.parse(response).message != 1 && JSON.parse(response).message != '') {
					$('#attFailAlertMsg').html(JSON.parse(response).message);
					$('#attFailAlert').removeClass('clear').show();
					$('.loginBtns').hide();
				} else {
					$('.loginBtns').show();
				}

			}

			$('#loadingImgAtt').addClass('displayNone');
			$('#loadingImgAtt').hide();
		} 
	}); 
});


