// AJAX Code To Submit Feedbacks.
$(document).on( "click","button.fbSbm", function( event ) {		
	var empRate =$(this).closest('form').find('input.clickStarVal').val();
	var feedBk  =$(this).closest('form').find('textarea#fbArea').val().trim();
	var isError = false;
	if (empRate.length == 0){		
	$("div.starWrapper i.fa-star").addClass("fa-star-o");
	$("div.starWrapper i.fa-star").removeClass("fa-star");
	$("div.starWrapper i.fa-star-o").css('color','#CE5454');
	isError = true;
	}
	if (feedBk.length == 0){		
		 $(this).closest('form').find('textarea#fbArea').addClass('highlight_item_error');	
		isError = true;		 
		}else{		
		$(this).closest('form').find('textarea#fbArea').removeClass('highlight_item_error');
		}

		if(isError){return false;}else{
		$('#fbloadingImg').removeClass('displayNone');
	   $('#fbloadingImg').show();

			var str = { rating: empRate, feedBack: feedBk };
			$.ajax({
				type: "POST",
				dataType: 'json',
				data :  str,
				url: "/dashboard/saveFeedBack",	
				async:true,
				cache: false,
				success: function(response){ 
					 $('#fbloadingImg').addClass('displayNone');
					 $('#fbloadingImg').hide(); 
					if(response == true){
						$('#fbSuccessAlert').removeClass('clear');
						$('#fbSuccessAlertMsg').html("Thanks for your feedback.");
						$('#fbFailAlert').addClass('clear');
						$('#fbSuccessAlert').fadeIn();
						setTimeout(function(){$('#fbSuccessAlert').fadeOut("slow");}, 2000);
							$('div.popover').popover('hide');			
						}

					} 
			}); 
		}
	});

	$('body').on('hidden.bs.popover', function (e) {
		$(e.target).data("bs.popover").inState.click = false;
	});


	$('[data-toggle=popover]').click(function (e) {
		$("div.starWrapper i.fa-star").addClass("fa-star-o");
		$("div.starWrapper i.fa-star").removeClass("fa-star");
		e.stopPropagation();
	});
	$(document).click(function (e) {
		if (($('.popover').has(e.target).length == 0) || $(e.target).is('.close')) {
			$('[data-toggle=popover]').popover('hide');
		}
	});



});

	function isIE() {
	  ua = navigator.userAgent;
	  var is_ie = ua.indexOf("MSIE ") > -1 || ua.indexOf("Trident/") > -1;
	  return is_ie; 
	}/**/
